(2055, '<p> </p>\r\n<p style=\"margin-bottom: 0cm; line-height: 100%;\">Jaki wynik zostanie wyświetlony(podaj liczbe)?</p>\r\n<p> </p>\r\n<p style=\"margin-bottom: 0cm; line-height: 100%;\">int a=6,b=5;</p>\r\n<p> </p>\r\n<p style=\"margin-bottom: 0cm; line-height: 100%;\">if (a%b!=0) cout&lt;&lt;a;</p>\r\n<p> </p>\r\n<p style=\"margin-bottom: 0cm; line-height: 100%;\">else cout&lt;&lt;b;</p>\r\n<p> </p>\r\n<p></p>', '5', '', '', '', '', '', 0, 1, 'INNE', 0, NULL, NULL, NULL, 2019, 1, '1', 0, 1, 30),
(2052, '<p> </p>\r\n<p style=\"margin-bottom: 0cm; line-height: 100%;\">Podaj typ danych przeznaczony do liczb rzeczywistych</p>\r\n<p> </p>\r\n<p></p>', 'float || double', '', '', '', '', '', 0, 1, 'INNE', 0, NULL, NULL, NULL, 2019, 1, '1', 0, 1, 30),
(2056, '<p> </p>\r\n<p style=\"margin-bottom: 0cm; line-height: 100%;\">Podaj ile będzie wynosić z po wykonaniu poniższych instrukcji.</p>\r\n<p> </p>\r\n<p style=\"margin-bottom: 0cm; line-height: 100%;\">int z=2;</p>\r\n<p> </p>\r\n<p style=\"margin-bottom: 0cm; line-height: 100%;\">for(int i=0;i&gt;2;i++)</p>\r\n<p> </p>\r\n<p style=\"margin-bottom: 0cm; line-height: 100%;\">z=z*2;</p>\r\n<p> </p>\r\n<p></p>', '2', '', '', '', '', '', 0, 1, 'INNE', 0, NULL, NULL, NULL, 2019, 1, '1', 0, 1, 30),
(2057, '<p> </p>\r\n<p style=\"margin-bottom: 0cm; line-height: 100%;\">int a=10;</p>\r\n<p> </p>\r\n<p style=\"margin-bottom: 0cm; line-height: 100%;\">int t[a];</p>\r\n<p> </p>\r\n<p style=\"margin-bottom: 0cm; line-height: 100%;\">Podaj zmienną odpowiedzialną za ostatni element tablicy.</p>\r\n<p> </p>\r\n<p></p>', 't[9]||t[a-1]', '', '', '', '', '', 0, 1, 'INNE', 0, NULL, NULL, NULL, 2019, 1, '1', 0, 1, 30),
(2058, '<p> </p>\r\n<p style=\"margin-bottom: 0cm; line-height: 100%;\">Napisz w której pętli warunek jest sprawdzany na początku pętli</p>\r\n<p> </p>\r\n<p></p>', 'while', '', '', '', '', '', 0, 1, 'INNE', 0, NULL, NULL, NULL, 2019, 1, '1', 0, 1, 30),
(2059, '<p> </p>\r\n<p style=\"margin-bottom: 0cm; line-height: 100%;\"><span style=\"background: transparent;\">Napisz pętlę for, wykorzystującą zmienną i (int już zadeklarowaną), wyświetlającą liczby od 10 do 0 co jedną. Każda liczba w nowej linii.</span></p>\r\n<p> </p>\r\n<p style=\"margin-bottom: 0cm; line-height: 100%;\"> </p>\r\n<p> </p>\r\n<p> </p>', 'for(i=10;i&gt;=0;i--) cout&lt;&lt;i&lt;&lt;;endl;', '', '', '', '', '', 0, 1, 'INNE', 0, NULL, NULL, NULL, 2019, 1, '1', 0, 1, 30),
(2060, '<p> </p>\r\n<p style=\"margin-bottom: 0cm; line-height: 100%;\"><span style=\"background: transparent;\">Napisz pętlę while, wykorzystującą zmienną i=10 (int już zadeklarowaną), wyświetlającą liczby od 10 do 0 co jedną. Każda liczba w nowej linii.</span></p>\r\n<p> </p>\r\n<p style=\"margin-bottom: 0cm; line-height: 100%;\"> </p>\r\n<p> </p>\r\n<p> </p>', 'while(i&lt;=0) { cout&lt;i&lt;endl; i--; }', '', '', '', '', '', 0, 1, 'INNE', 0, NULL, NULL, NULL, 2019, 1, '1', 0, 1, 30),
(2061, '<p> </p>\r\n<p style=\"margin-bottom: 0cm; line-height: 100%;\"><span style=\"background: transparent;\">Podaj wynik k po wykonaniu poniższych poleceń</span></p>\r\n<p> </p>\r\n<p style=\"margin-bottom: 0cm; line-height: 100%;\"> </p>\r\n<p> </p>\r\n<p style=\"margin-bottom: 0cm; line-height: 100%;\"><span style=\"background: transparent;\">a=10;</span></p>\r\n<p> </p>\r\n<p style=\"margin-bottom: 0cm; line-height: 100%;\"><span style=\"background: transparent;\">k=2;</span></p>\r\n<p> </p>\r\n<p style=\"margin-bottom: 0cm; line-height: 100%;\"><span style=\"background: transparent;\">while(a&lt;0)</span></p>\r\n<p> </p>\r\n<p style=\"margin-bottom: 0cm; line-height: 100%;\"><span style=\"background: transparent;\">{ k=k+2;</span></p>\r\n<p> </p>\r\n<p style=\"margin-bottom: 0cm; line-height: 100%;\"><span style=\"background: transparent;\"> a++;</span></p>\r\n<p> </p>\r\n<p style=\"margin-bottom: 0cm; line-height: 100%;\"><span style=\"background: transparent;\">}</span></p>\r\n<p> </p>\r\n<p></p>', '2', '', '', '', '', '', 0, 1, 'INNE', 0, NULL, NULL, NULL, 2019, 1, '1', 0, 1, 30),
(2062, '<p> </p>\r\n<p style=\"margin-bottom: 0cm; line-height: 100%;\"><span style=\"background: transparent;\">Podaj wynik zmiennej a po wykonaniu poniższych poleceń</span></p>\r\n<p> </p>\r\n<p style=\"margin-bottom: 0cm; line-height: 100%;\"> </p>\r\n<p> </p>\r\n<p style=\"margin-bottom: 0cm; line-height: 100%;\"><span style=\"background: transparent;\">a=-1;</span></p>\r\n<p> </p>\r\n<p style=\"margin-bottom: 0cm; line-height: 100%;\"><span style=\"background: transparent;\">k=2;</span></p>\r\n<p> </p>\r\n<p style=\"margin-bottom: 0cm; line-height: 100%;\"><span style=\"background: transparent;\">while(a&lt;0)</span></p>\r\n<p> </p>\r\n<p style=\"margin-bottom: 0cm; line-height: 100%;\"><span style=\"background: transparent;\">{ k=k+2;</span></p>\r\n<p> </p>\r\n<p style=\"margin-bottom: 0cm; line-height: 100%;\"><span style=\"background: transparent;\"> a++;</span></p>\r\n<p> </p>\r\n<p style=\"margin-bottom: 0cm; line-height: 100%;\"><span style=\"background: transparent;\">}</span></p>\r\n<p> </p>\r\n<p></p>', '4', '', '', '', '', '', 0, 1, 'INNE', 0, NULL, NULL, NULL, 2019, 1, '1', 0, 1, 30),
(2063, '<p> </p>\r\n<p style=\"margin-bottom: 0cm; line-height: 100%;\"><span style=\"background: transparent;\">Ile razy zostanie wykonana poniższa pętla:</span></p>\r\n<p> </p>\r\n<p style=\"margin-bottom: 0cm; line-height: 100%;\"><span style=\"background: transparent;\">for(i=0;i&lt;5;i++){}</span></p>\r\n<p> </p>\r\n<p style=\"margin-bottom: 0cm; line-height: 100%;\"> </p>\r\n<p> </p>\r\n<p></p>', '5', '', '', '', '', '', 0, 1, 'INNE', 0, NULL, NULL, NULL, 2019, 1, '1', 0, 1, 30),
(2068, '<p> </p>\r\n<p style=\"margin-bottom: 0cm; line-height: 100%;\"><span style=\"background: transparent;\">int a;</span></p>\r\n<p> </p>\r\n<p style=\"margin-bottom: 0cm; line-height: 100%;\"><span style=\"background: transparent;\">Podaną zmienną wyświetl na 2 sposoby.</span></p>\r\n<p> </p>\r\n<p></p>', 'cout<<a; printf(„%d”,a);', '', '', '', '', '', 0, 1, 'E12', NULL, NULL, NULL, NULL, 2019, 1, '1', 0, 1, 30),
(2067, '<p> </p>\r\n<p style=\"margin-bottom: 0cm; line-height: 100%;\"><span style=\"background: transparent;\">int a;</span></p>\r\n<p> </p>\r\n<p style=\"margin-bottom: 0cm; line-height: 100%;\"><span style=\"background: transparent;\">Podaną zmienną wczytaj na 2 sposoby.</span></p>\r\n<p> </p>\r\n<p></p>', 'cin>>a; ||  scanf(„%d”,&a);', '', '', '', '', '', 0, 1, 'INNE', NULL, NULL, NULL, NULL, 2019, 1, '1', 0, 1, 30),
